/**
 * Plugin to add line numbers
 *
 * To add line numbers to a highlighted code block
 * just add the attr data-linenums="1" to the pre element that
 * wraps the code block.
 * You can also set the starting line number by adding the attr
 * data-linenums-start="[YOUR STARTING NUMBER]" to the pre element
 *
 * @important THE show-invisibles PLUGIN IS REQUIRED FOR THIS PLUGIN TO WORK PROPERLY
 *
 * @example <pre data-linenums="1"><code>[YOUR CODE]</code></pre>
 * @example <pre data-linenums="1" data-linenums-start="13"><code>[YOUR CODE]</code></pre>
 *
 * @author Justin Carlson <env.justin@gmail.com>
 * @created 12/16/2012
 *
 */(function(){if(!window.Prism)return;var e=!1,t=null,n=null,r=document.createElement("div");r.className="linenumWrap";Prism.hooks.add("before-highlight",function(i){e=i.element.parentNode.getAttribute("data-linenums");if(e){r.innerHTML="";n=i.element.parentNode.getAttribute("data-linenums-start");t=n?n:1}});Prism.hooks.add("wrap",function(n){if(e){console.log(n.content);var i=n.content.match(/\r\n|\r|\n/g)?n.content.match(/\r\n|\r|\n/g).length:0;console.log(i);for(var s=0;s<i;s++)r.innerHTML+='<div class="linenum">'+ ++t+"</div>"}});Prism.hooks.add("after-highlight",function(t){if(e){n=n?n:1;r.innerHTML='<div class="linenum">'+n+"</div>"+r.innerHTML;t.element.parentNode.appendChild(r.cloneNode(!0))}})})();